#include <stdio.h>
#include <string.h>

int main() {
    // --- Dados para a primeira carta ---
        char estado1;
            char nome_cidade1[50];
                char codigo_carta1[4];
                    int populacao1;
                        float area1;
                            float pib1;
                                int pontos_turisticos1;
                                    float densidade_demografica1;

                                        // --- Dados para a segunda carta ---
                                            char estado2;
                                                char nome_cidade2[50];
                                                    char codigo_carta2[4];
                                                        int populacao2;
                                                            float area2;
                                                                float pib2;
                                                                    int pontos_turisticos2;
                                                                        float densidade_demografica2;

                                                                            int escolha;
                                                                                char atributo_usado[50];
                                                                                    float valor1, valor2;
                                                                                        char *vencedor; 

                                                                                            // --- ENTRADA DE DADOS PARA A PRIMEIRA CARTA ---
                                                                                                printf("--- 1/2. CADASTRO DA PRIMEIRA CARTA ---\n");
                                                                                                    
                                                                                                        printf("Estado (apenas 1 letra): ");
                                                                                                            scanf(" %c", &estado1); 

                                                                                                                printf("Nome da Cidade: ");
                                                                                                                    scanf(" %49[^\n]", nome_cidade1);
                                                                                                                        
                                                                                                                            printf("Codigo da Carta (max 3 caracteres): ");
                                                                                                                                scanf(" %3s", codigo_carta1);

                                                                                                                                    printf("Populacao (apenas numeros inteiros): ");
                                                                                                                                        scanf("%d", &populacao1);
                                                                                                                                            
                                                                                                                                                printf("Area em km2 (ex: 1521.11): ");
                                                                                                                                                    scanf("%f", &area1);
                                                                                                                                                        
                                                                                                                                                            printf("PIB em bilhoes (ex: 699.28): ");
                                                                                                                                                                scanf("%f", &pib1);
                                                                                                                                                                    
                                                                                                                                                                        printf("Pontos Turisticos (apenas numeros inteiros): ");
                                                                                                                                                                            scanf("%d", &pontos_turisticos1);
                                                                                                                                                                                
                                                                                                                                                                                    printf("\n");

                                                                                                                                                                                        // --- ENTRADA DE DADOS PARA A SEGUNDA CARTA ---
                                                                                                                                                                                            printf("--- 2/2. CADASTRO DA SEGUNDA CARTA ---\n");
                                                                                                                                                                                                
                                                                                                                                                                                                    printf("Estado (apenas 1 letra): ");
                                                                                                                                                                                                        scanf(" %c", &estado2);

                                                                                                                                                                                                            printf("Nome da Cidade: ");
                                                                                                                                                                                                                scanf(" %49[^\n]", nome_cidade2);
                                                                                                                                                                                                                    
                                                                                                                                                                                                                        printf("Codigo da Carta (max 3 caracteres): ");
                                                                                                                                                                                                                            scanf(" %3s", codigo_carta2);

                                                                                                                                                                                                                                printf("Populacao (apenas numeros inteiros): ");
                                                                                                                                                                                                                                    scanf("%d", &populacao2);
                                                                                                                                                                                                                                        
                                                                                                                                                                                                                                            printf("Area em km2 (ex: 1200.25): ");
                                                                                                                                                                                                                                                scanf("%f", &area2);
                                                                                                                                                                                                                                                    
                                                                                                                                                                                                                                                        printf("PIB em bilhoes (ex: 300.50): ");
                                                                                                                                                                                                                                                            scanf("%f", &pib2);
                                                                                                                                                                                                                                                                
                                                                                                                                                                                                                                                                    printf("Pontos Turisticos (apenas numeros inteiros): ");
                                                                                                                                                                                                                                                                        scanf("%d", &pontos_turisticos2);
                                                                                                                                                                                                                                                                            
                                                                                                                                                                                                                                                                                printf("\n");

                                                                                                                                                                                                                                                                                    // --- VALIDAÇÃO DE ÁREA E CÁLCULO DA DENSIDADE ---
                                                                                                                                                                                                                                                                                        if (area1 <= 0 || area2 <= 0) {
                                                                                                                                                                                                                                                                                                printf("Erro: a area nao pode ser zero ou negativa para calcular a Densidade Demografica.\n");
                                                                                                                                                                                                                                                                                                        return 1;
                                                                                                                                                                                                                                                                                                            }

                                                                                                                                                                                                                                                                                                                densidade_demografica1 = populacao1 / area1; 
                                                                                                                                                                                                                                                                                                                    densidade_demografica2 = populacao2 / area2;

                                                                                                                                                                                                                                                                                                                        // --- EXIBIÇÃO RÁPIDA DAS CARTAS ANTES DO JOGO ---
                                                                                                                                                                                                                                                                                                                            printf("Cartas cadastradas:\n");
                                                                                                                                                                                                                                                                                                                                printf("- Estado: %c, Cidade: %s, Cod: %s\n", estado1, nome_cidade1, codigo_carta1);
                                                                                                                                                                                                                                                                                                                                    printf("- Estado: %c, Cidade: %s, Cod: %s\n\n", estado2, nome_cidade2, codigo_carta2);
                                                                                                                                                                                                                                                                                                                                        
                                                                                                                                                                                                                                                                                                                                            // --- MENU INTERATIVO (switch) ---
                                                                                                                                                                                                                                                                                                                                                printf("--- MENU DE COMPARACAO ---\n");
                                                                                                                                                                                                                                                                                                                                                    printf("Escolha o atributo para comparacao (MAIOR valor vence, exceto Densidade Demografica):\n");
                                                                                                                                                                                                                                                                                                                                                        printf("1. Populacao\n");
                                                                                                                                                                                                                                                                                                                                                            printf("2. Area\n");
                                                                                                                                                                                                                                                                                                                                                                printf("3. PIB\n");
                                                                                                                                                                                                                                                                                                                                                                    printf("4. Pontos Turisticos\n");
                                                                                                                                                                                                                                                                                                                                                                        printf("5. Densidade Demografica (MENOR valor vence)\n");
                                                                                                                                                                                                                                                                                                                                                                            printf("Escolha (1-5): ");
                                                                                                                                                                                                                                                                                                                                                                                
                                                                                                                                                                                                                                                                                                                                                                                    if (scanf("%d", &escolha) != 1) {
                                                                                                                                                                                                                                                                                                                                                                                            escolha = 0; 
                                                                                                                                                                                                                                                                                                                                                                                                }
                                                                                                                                                                                                                                                                                                                                                                                                    
                                                                                                                                                                                                                                                                                                                                                                                                        // Limpeza de buffer
                                                                                                                                                                                                                                                                                                                                                                                                            while (getchar() != '\n'); 
                                                                                                                                                                                                                                                                                                                                                                                                                
                                                                                                                                                                                                                                                                                                                                                                                                                    // --- LÓGICA DE COMPARAÇÃO (if-else dentro do switch) ---
                                                                                                                                                                                                                                                                                                                                                                                                                        switch (escolha) {
                                                                                                                                                                                                                                                                                                                                                                                                                                case 1: // Populacao - Maior valor vence
                                                                                                                                                                                                                                                                                                                                                                                                                                            strcpy(atributo_usado, "Populacao");
                                                                                                                                                                                                                                                                                                                                                                                                                                                        valor1 = (float)populacao1;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                    valor2 = (float)populacao2;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                if (populacao1 > populacao2) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                vencedor = nome_cidade1;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            } else if (populacao2 > populacao1) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            vencedor = nome_cidade2;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        } else {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        vencedor = "Empate!";
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                break;

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        case 2: // Area - Maior valor vence
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    strcpy(atributo_usado, "Area");
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                valor1 = area1;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            valor2 = area2;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        if (area1 > area2) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        vencedor = nome_cidade1;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    } else if (area2 > area1) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    vencedor = nome_cidade2;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                } else {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                vencedor = "Empate!";
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        break;

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                case 3: // PIB - Maior valor vence
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            strcpy(atributo_usado, "PIB");
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        valor1 = pib1;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    valor2 = pib2;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                if (pib1 > pib2) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                vencedor = nome_cidade1;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            } else if (pib2 > pib1) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            vencedor = nome_cidade2;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        } else {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        vencedor = "Empate!";
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                break;

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        case 4: // Pontos Turisticos - Maior valor vence
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    strcpy(atributo_usado, "Pontos Turisticos");
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                valor1 = (float)pontos_turisticos1;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            valor2 = (float)pontos_turisticos2;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        if (pontos_turisticos1 > pontos_turisticos2) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        vencedor = nome_cidade1;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    } else if (pontos_turisticos2 > pontos_turisticos1) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    vencedor = nome_cidade2;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                } else {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                vencedor = "Empate!";
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        break;

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                case 5: // Densidade Demografica - MENOR valor vence
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            strcpy(atributo_usado, "Densidade Demografica");
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        valor1 = densidade_demografica1;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    valor2 = densidade_demografica2;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                if (densidade_demografica1 < densidade_demografica2) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                vencedor = nome_cidade1;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            } else if (densidade_demografica2 < densidade_demografica1) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            vencedor = nome_cidade2;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        } else {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        vencedor = "Empate!";
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                break;

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        default:
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    strcpy(atributo_usado, "Nao selecionado");
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                vencedor = "Opcao invalida. O programa sera encerrado.";
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            printf("\n%s\n", vencedor);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        return 0;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    // --- EXIBIÇÃO DOS RESULTADOS ---
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        printf("\n--- RESULTADO DA COMPARACAO ---\n");
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                if (escolha >= 1 && escolha <= 5) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        printf("1. Cidades: %s (Estado: %c) vs %s (Estado: %c)\n", nome_cidade1, estado1, nome_cidade2, estado2);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                printf("2. Atributo usado: %s\n", atributo_usado);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                // 3. Exibicao dos valores formatados
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        printf("3. Valores do atributo para cada cidade:\n");
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                if (escolha == 1 || escolha == 4) { // Inteiros (População, Pontos Turísticos)
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            printf("   %s: %.0f\n", nome_cidade1, valor1);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        printf("   %s: %.0f\n", nome_cidade2, valor2);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                } else if (escolha == 3) { // PIB
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            printf("   %s: %.2f bilhoes\n", nome_cidade1, valor1);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        printf("   %s: %.2f bilhoes\n", nome_cidade2, valor2);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                } else { // Area, Densidade Demografica
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            printf("   %s: %.2f\n", nome_cidade1, valor1);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        printf("   %s: %.2f\n", nome_cidade2, valor2);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                printf("4. Vencedor: %s\n\n", vencedor);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    } else {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            printf("%s\n\n", vencedor);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                }

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    return 0;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
